import React, { useState } from 'react';
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  ComposedChart,
  Bar
} from 'recharts';
import { motion } from 'framer-motion';

interface ChartDataPoint {
  timestamp: string;
  price: number;
  volume: number;
  marketCap: number;
  change24h?: number;
}

interface CoinGeckoChartProps {
  data: ChartDataPoint[];
  title: string;
  color?: string;
  height?: number;
  showVolume?: boolean;
}

export const CoinGeckoChart: React.FC<CoinGeckoChartProps> = ({
  data,
  title,
  color = '#16a34a',
  height = 400,
  showVolume = true
}) => {
  const [activeChart, setActiveChart] = useState<'price' | 'marketCap' | 'volume'>('price');
  const [timeframe, setTimeframe] = useState<'1D' | '7D' | '30D' | '90D' | '1Y'>('30D');

  const formatCurrency = (value: number) => {
    if (value >= 1000000000) return `$${(value / 1000000000).toFixed(2)}B`;
    if (value >= 1000000) return `$${(value / 1000000).toFixed(2)}M`;
    if (value >= 1000) return `$${(value / 1000).toFixed(2)}K`;
    return `$${value.toFixed(2)}`;
  };

  const formatVolume = (value: number) => {
    if (value >= 1000000000) return `${(value / 1000000000).toFixed(1)}B`;
    if (value >= 1000000) return `${(value / 1000000).toFixed(1)}M`;
    if (value >= 1000) return `${(value / 1000).toFixed(1)}K`;
    return value.toFixed(0);
  };

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric',
      hour: timeframe === '1D' ? 'numeric' : undefined,
      minute: timeframe === '1D' ? '2-digit' : undefined
    });
  };

  const getCurrentValue = () => {
    if (data.length === 0) return 0;
    const latest = data[data.length - 1];
    switch (activeChart) {
      case 'price': return latest.price;
      case 'marketCap': return latest.marketCap;
      case 'volume': return latest.volume;
      default: return latest.price;
    }
  };

  const getChange24h = () => {
    if (data.length < 2) return 0;
    const latest = data[data.length - 1];
    const previous = data[data.length - 2];
    const currentValue = getCurrentValue();
    const previousValue = activeChart === 'price' ? previous.price : 
                         activeChart === 'marketCap' ? previous.marketCap : previous.volume;
    return ((currentValue - previousValue) / previousValue) * 100;
  };

  const change24h = getChange24h();
  const isPositive = change24h >= 0;

  return (
    <motion.div 
      className="bg-gray-800 rounded-xl border border-gray-700 p-6"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-xl font-bold text-white mb-2">{title}</h3>
          <div className="flex items-center gap-4">
            <span className="text-2xl font-bold text-white">
              {activeChart === 'volume' ? formatVolume(getCurrentValue()) : formatCurrency(getCurrentValue())}
            </span>
            <span className={`text-sm font-medium px-2 py-1 rounded ${
              isPositive ? 'text-green-400 bg-green-900/30' : 'text-red-400 bg-red-900/30'
            }`}>
              {isPositive ? '+' : ''}{change24h.toFixed(2)}%
            </span>
          </div>
        </div>

        {/* Timeframe Selector */}
        <div className="flex bg-gray-700 rounded-lg p-1">
          {['1D', '7D', '30D', '90D', '1Y'].map((tf) => (
            <button
              key={tf}
              onClick={() => setTimeframe(tf as any)}
              className={`px-3 py-1 text-xs font-medium rounded transition-colors ${
                timeframe === tf
                  ? 'bg-gray-600 text-white shadow-sm'
                  : 'text-gray-300 hover:text-white'
              }`}
            >
              {tf}
            </button>
          ))}
        </div>
      </div>

      {/* Chart Type Selector */}
      <div className="flex gap-2 mb-4">
        {[
          { key: 'price', label: 'Price', color: '#16a34a' },
          { key: 'marketCap', label: 'Market Cap', color: '#2563eb' },
          { key: 'volume', label: 'Volume', color: '#dc2626' }
        ].map((chart) => (
          <button
            key={chart.key}
            onClick={() => setActiveChart(chart.key as any)}
            className={`px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
              activeChart === chart.key
                ? 'bg-blue-900/30 text-blue-400 border border-blue-500/50'
                : 'text-gray-400 hover:text-white hover:bg-gray-700'
            }`}
          >
            {chart.label}
          </button>
        ))}
      </div>

      {/* Chart */}
      <div style={{ height }}>
        <ResponsiveContainer width="100%" height="100%">
          {showVolume && activeChart === 'price' ? (
            <ComposedChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <defs>
                <linearGradient id="colorPrice" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor={color} stopOpacity={0.3}/>
                  <stop offset="95%" stopColor={color} stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis 
                dataKey="timestamp" 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={formatDate}
              />
              <YAxis 
                yAxisId="price"
                orientation="right"
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={formatCurrency}
              />
              <YAxis 
                yAxisId="volume"
                orientation="left"
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={formatVolume}
              />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: 'white', 
                  border: '1px solid #e5e7eb',
                  borderRadius: '8px',
                  boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
                }}
                formatter={(value: any, name: string) => [
                  name === 'price' ? formatCurrency(value) : formatVolume(value),
                  name === 'price' ? 'Price' : 'Volume'
                ]}
                labelFormatter={(label) => formatDate(label)}
              />
              <Bar 
                yAxisId="volume"
                dataKey="volume" 
                fill="#e5e7eb" 
                opacity={0.3}
                radius={[2, 2, 0, 0]}
              />
              <Area
                yAxisId="price"
                type="monotone"
                dataKey="price"
                stroke={color}
                strokeWidth={2}
                fill="url(#colorPrice)"
                dot={false}
              />
            </ComposedChart>
          ) : (
            <AreaChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <defs>
                <linearGradient id={`color${activeChart}`} x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor={color} stopOpacity={0.3}/>
                  <stop offset="95%" stopColor={color} stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis 
                dataKey="timestamp" 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={formatDate}
              />
              <YAxis 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={activeChart === 'volume' ? formatVolume : formatCurrency}
              />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: 'white', 
                  border: '1px solid #e5e7eb',
                  borderRadius: '8px',
                  boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
                }}
                formatter={(value: any) => [
                  activeChart === 'volume' ? formatVolume(value) : formatCurrency(value),
                  activeChart === 'price' ? 'Price' : activeChart === 'marketCap' ? 'Market Cap' : 'Volume'
                ]}
                labelFormatter={(label) => formatDate(label)}
              />
              <Area
                type="monotone"
                dataKey={activeChart}
                stroke={color}
                strokeWidth={2}
                fill={`url(#color${activeChart})`}
                dot={false}
              />
            </AreaChart>
          )}
        </ResponsiveContainer>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-3 gap-4 mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
        <div>
          <div className="text-xs text-gray-500 mb-1">24h Volume</div>
          <div className="font-semibold text-gray-900 dark:text-white">
            {data.length > 0 ? formatVolume(data[data.length - 1].volume) : '-'}
          </div>
        </div>
        <div>
          <div className="text-xs text-gray-500 mb-1">Market Cap</div>
          <div className="font-semibold text-gray-900 dark:text-white">
            {data.length > 0 ? formatCurrency(data[data.length - 1].marketCap) : '-'}
          </div>
        </div>
        <div>
          <div className="text-xs text-gray-500 mb-1">24h Change</div>
          <div className={`font-semibold ${isPositive ? 'text-green-600' : 'text-red-600'}`}>
            {isPositive ? '+' : ''}{change24h.toFixed(2)}%
          </div>
        </div>
      </div>
    </motion.div>
  );
};
